package operators;

public class Incerment_ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declare variables
		int a = 12, b= 12;
		int result1, result2;
		
		//original value
		System.out.println("Value of a:" + a);
		// incerment operator
		result1 = ++a;
		System.out.println("After incerment:" + result1);
		
		System.out.println("Value of b:" + b);
		// decrement operator
		result2 = --b;
		System.out.println("After decerment:" + result2);
	}

}
